define('gcrm-util',['app'], function(app) {
  app.registerFactory('GCRMUtil', [
    '$rootScope',
    function($rootScope) {
      var GCRM = window.GCRM;
      /*for dev*/
      if (!GCRM) {
        GCRM = {};
        require(['../../data/GCRM_MESSAGE'], function (response) {
          GCRM.messages = response;
        });
      }
      return {
        decodeGCRMMessageSingle: function (code) {
          if (!GCRM || !GCRM.messages) {
            return '';
          }
          return GCRM.messages[code];
        },
        decodeGCRMMessages: function (codeArray, messageSplitter) {
          if (!GCRM || !GCRM.messages) {
            return '';
          }
          var splitter = messageSplitter || ',';
          var decodeArr = [];
          for (var i = 0; i < codeArray.length; i++) {
            decodeArr.push(GCRM.messages[codeArray[i]]);
          }
          return decodeArr.join(splitter);
        },
        decodeGCRMError: function (errorObject, messageSplitter) {
          if (!GCRM || !GCRM.messages) {
            return '';
          }
          var _this = this;
          var splitter = messageSplitter || ',';
          var returnArr = [];
          angular.forEach(errorObject, function (value, key) {
            returnArr.push(_this.decodeGCRMMessageSingle(value));
          });
          return returnArr.join(splitter);
        },
        translate: function (code, args) {
          var message = GCRM.messages[code];
          var result = this.substitute(message, args);
          return result || "";
        },
        substitute: function (text, obj) {
          if (!text) {
            return text;
          }
          for (var key in obj) {
            text = text.replace(new RegExp("\\{" + key + "\\}", "g"), obj[key]);
          }
          return text;
        },
        checkOwnerPermit: function (operKey, isOwner) {
          var OwnerOpers = $rootScope.OwnerOpers;
          var needPermit = OwnerOpers[operKey];

          return (!needPermit || isOwner);
        }
      };
    }
  ]);
});

define( 'app-http-interceptor',[ 'app', 'gcrm-util' ], function ( app ) {
  app.config( [ '$httpProvider',
    function ( $httpProvider ) {
      var errorInterceptor = [ '$q', '$log', 'GCRMUtil', '$location',
        function ( $q, $log, GCRMUtil, $location ) {
          function modalAlert( message ) {
            var $modal = $(
              '<div tabindex="-1" class="modal fade ngAlert in" ng-class="{in: animate}" style="z-index: 1050; display: block;">' +
              '<div class="modal-dialog">' +
              '<div class="modal-content">\n' +
              '  <div class="modal-header">\n' +
              '   <h4 class="modal-title">' + message + '</h4>\n' +
              '  </div>\n' +
              '  <div class="modal-body">\n' +
              '    <button type="button" class="btn btn-primary btn-interceptor-ok">OK</button>\n' +
              '  </div>\n' +
              '</div>' +
              '</div>' +
              '</div>');
            var $backdrop = $('<div class="modal-backdrop in" style="z-index: 1040;"></div>');
            $modal.appendTo($('body'));
            $backdrop.appendTo($("body"));
            $modal.fadeIn(500);
            $(".btn-interceptor-ok").on('click', function () {
              $modal.remove();
              $backdrop.remove();
            });
          }
          function getInternetExplorerVersion() {
            var rv = -1,
              ua,
              re;
            if (navigator.appName == 'Microsoft Internet Explorer')
            {
              ua = navigator.userAgent;
              re  = new RegExp("MSIE ([0-9]{1,}[.0-9]{0,})");
              if (re.exec(ua) != null) {
                rv = parseFloat( RegExp.$1 );
              }
            }
            else if (navigator.appName == 'Netscape')
            {
              ua = navigator.userAgent;
              re  = new RegExp("Trident/.*rv:([0-9]{1,}[.0-9]{0,})");
              if (re.exec(ua) != null) {
                rv = parseFloat( RegExp.$1 );
              }
            }
            return rv;
          }
          var msie = getInternetExplorerVersion();
          // try {
          //   msie = ~~( navigator.userAgent.toLowerCase().match( /msie (\d)/ ) || [] )[ 1 ];
          // } catch ( e ) {
          // }
          // msie = 1;
          return {
            'request': function ( config ) {
              if ( msie > 0 && config.method === 'GET' && config.url && !/\.js$|\.html$/.test(config.url) ) {
                config.params = config.params || {};
                config.params['_'] = new Date().getTime();
              }
              return config;
            },
            'response': function ( response ) {
              /**
                code
                200
                202errorserrorsdom
                201messagemessagedomalert
                203errorsmessage
              */
              if ( response.data && response.data.code && response.data.code !== 200 ) {
                if ( response.data.code === 202) {
                  modalAlert( GCRMUtil.decodeGCRMError( response.data.errors ) );
                } else if ( response.data.code === 201 ) {
                  modalAlert( response.data.message );
                } else if ( response.data.code === 203 ) {
                  modalAlert( response.data.message );
                  modalAlert( GCRMUtil.decodeGCRMError( response.data.errors ) );
                } else if ( response.data.code === 208 ) {
                  //208session
                  window.location.href = '/gcrm/login';
                }
              }
              return response;
            },
            'responseError': function ( rejection ) {
              $log.error( rejection );
              var status = rejection.status.toString();
              if ( status.indexOf( '4' ) === 0 ) {
                // $location.path('/error/404');
              } else if ( status.indexOf( '5' ) === 0 ) {
                // $location.path('/error/500');
              }

              return $q.reject( rejection );
            }
          };
        }
      ];
      $httpProvider.interceptors.push( errorInterceptor );
    }
  ]);
});
