define('_common/modal/CtrlModal',['app'], function ( app ) {
  app.registerController('CtrlModal', ['$scope', '$modalInstance', 'opts', '$filter',function ($scope, $modalInstance, opts, $filter) {
    var translateFilter = $filter('translate');
    $scope.title = opts.title;
    $scope.content = opts.content;
    $scope.contentList = opts.contentList;
    $scope.okText = opts.okText || translateFilter('CONFIRM');
    $scope.cancelText = opts.cancelText || translateFilter('CANCEL');
    $scope.nextText = opts.nextText || translateFilter('RETURN');
    $scope.showCancel = opts.showCancel || false;
    $scope.showReturn = opts.showReturn || false;
    $scope.modalDatas = opts.modalDatas;
    $scope.errors = opts.errors;

    //
    if ( opts.psInfo ) {
        $scope.psInfo = translateFilter('PS') + opts.psInfo;
    }
    $scope.ok = function () {
        $modalInstance.close('ok');
    };

    $scope.cancel = function () {
        $modalInstance.dismiss('cancel');
    };

    $scope.back = function () {
        $modalInstance.close('back');
    };

  }]);
});
define('_common/modal/Modal',['app'], function (app) {
  app.registerFactory('Modal', [
    '$modal',
    '$timeout',
    '$filter',
    'GCRMUtil',
    'STATIC_DIR',
    function ( $modal, $timeout, $filter, GCRMUtil, STATIC_DIR ) {
      return {
        alert: function (opts, cbfn) {
          var modalInstance,
            optsDefault = {
              title: $filter('translate')('ALERT'),
              content: ''
            };
          $timeout(function () {
            modalInstance = $modal.open({
              templateUrl: STATIC_DIR + 'app/_common/modal/modal.tpl.html',
              controller: 'CtrlModal',
              // backdrop : 'static',
              windowClass: 'ngAlert ' + opts.ngClass,
              resolve: {
                opts: function () {
                  return angular.extend(optsDefault, opts);
                }
              }
            });
            if (cbfn) {
              modalInstance.result.then(function (result) {
                cbfn();
              }, function () {
                cbfn();
              });
            }
          }, 0);
        },
        confirm: function (opts, cbfn) {
          var optsDefault = {
            title: $filter('translate')('CONFIRM'),
            content: '',
            showCancel: true
          };
          var modalInstance = $modal.open({
            templateUrl: STATIC_DIR + 'app/_common/modal/modal.tpl.html',
            controller: 'CtrlModal',
            // backdrop : 'static',
            windowClass: 'ngConfirm',
            resolve: {
              opts: function () {
                return angular.extend(optsDefault, opts);
              }
            }
          });
          modalInstance.result.then(function (result) {
            if (result === 'ok') {
              cbfn();
            }
          });
        },
        success: function (opts, cbfn) {
          var optsDefault = {
            title: '',
            content: ''
          };
          var modalInstance = $modal.open({
            templateUrl: STATIC_DIR + 'app/_common/modal/successModal.tpl.html',
            controller: 'CtrlModal',
            // backdrop : 'static',
            windowClass: 'successModal',
            resolve: {
              opts: function () {
                return angular.extend(optsDefault, opts);
              }
            }
          });

          $timeout(function () {
            modalInstance.dismiss('cancel');
            if (cbfn) {
              cbfn();
            }

          }, opts.timeOut || 2000);
        },
        error: function ( opts ) {
          var optsDefault = {
            title: $filter( 'translate' )( 'ALERT' )
          };
          var modalInstance = $modal.open({
            templateUrl: STATIC_DIR + 'app/_common/modal/errorModal.tpl.html',
            controller: 'CtrlModal',
            resolve: {
              opts: function () {
                return angular.extend( optsDefault, opts );
              }
            }
          });
        },
        showError: function ( errorList ) {
          var errorTextList, errors = [];
          angular.forEach( errorList, function ( value, key ) {
            errorTextList = [];
            angular.forEach( value, function ( value ) {
              errorTextList.push( value );
            });
            errors.push({
              typeName: GCRMUtil.decodeGCRMMessageSingle( key ),
              list: errorTextList
            });
          });
          this.error( {
            errors: errors
          });
        }
      };
    }
  ]);

});
